<script lang="ts" setup>
import { ref, reactive, onMounted, toRefs } from "vue";
import { useDevice } from "@/composition/device";

const props = defineProps({
    width: {
        type: String,
        default: "350px",
    },
    height: {
        type: String,
        default: "100px",
    },
    withAnimation: {
        type: Boolean,
        default: false,
    }
});

const { width, height, withAnimation } = toRefs(props);
const { isMobile } = useDevice();

const COEFFICIENT = .01;
const MAX_X = 6;
const MIN_X = -5;
const svg = ref<HTMLElement | null>(null);
const svgMiddle = reactive({
    x: 0,
    y: 0,
});
const mouse = reactive({
    x: 0,
    y: 0,
});
const lettersX = ref("0px")
const lettersY = ref("0px");
const deskX = ref("0px");
const deskY = ref("0px")

function listener(e: MouseEvent) {
    mouse.x = e.x;
    mouse.y = e.y;

    const calculatedX = (mouse.x - svgMiddle.x) * COEFFICIENT;

    const x = calculatedX > 0 && calculatedX > MAX_X ? MAX_X :
        calculatedX < 0 && calculatedX < MIN_X ? MIN_X 
        : calculatedX;
    const y = (mouse.y - svgMiddle.y) * COEFFICIENT;

    lettersX.value = `${x}px`
    lettersY.value = `${y}px`
    deskX.value = x > 0 ? `-${x}px` : `${Math.abs(x)}px`;
    deskY.value = y > 0 ? `-${y}px` : `${Math.abs(y)}px`;
}

function init() {
    const { width, height, x, y } = svg.value!.getBoundingClientRect();
    svgMiddle.x = x + (width / 2);
    svgMiddle.y = y + (height / 2);

    window.addEventListener("mousemove", listener);
}


onMounted(() => {
    if (withAnimation.value && !isMobile.value) {
        init();
    }
});
</script>

